import { Component, OnInit } from '@angular/core';
import { Chart, registerables } from 'chart.js';

@Component({
  selector: 'app-chart',
  templateUrl: './chart.component.html',
  styleUrls: ['./chart.component.css']
})
export class ChartComponent implements OnInit {
  ngOnInit(): void {
    Chart.register(...registerables); // Daftarkan semua modul Chart.js yang diperlukan

    const myChart = new Chart("myChart", {
      type: 'line', // Ubah tipe chart menjadi 'line'
      data: {
          labels: ['Point 1', 'Point 2', 'Point 3', 'Point 4', 'Point 5', 'Point 6'],
          datasets: [{
              label: 'Seismograph Data',
              data: [12, 19, 3, 5, 2, 3],
              borderColor: 'rgba(54, 162, 235, 1)',
              backgroundColor: 'rgba(54, 162, 235, 0.2)',
              borderWidth: 2,
              tension: 0.1, // Mengatur ketegangan garis untuk membuatnya lebih bergerigi
              fill: false, // Tidak mengisi di bawah garis
              pointRadius: 0, // Menghilangkan titik data
              borderDash: [5, 5], // Menambahkan dash untuk membuat efek seismograf lebih nyata
          }]
      },
      options: {
          scales: {
              y: {
                  beginAtZero: true
              }
          },
          elements: {
            line: {
              tension: 0.4 // Tambahkan ketegangan garis untuk membuatnya lebih mulus
            }
          }
      }
    });
  }
}
