import { Component, OnInit } from '@angular/core';
import { Chart, registerables } from 'chart.js';
import { HttpClient } from '@angular/common/http';
import { interval } from 'rxjs';

@Component({
  selector: 'app-chart',
  templateUrl: './chart.component.html',
  styleUrls: ['./chart.component.css']
})
export class ChartComponent implements OnInit {
  private apiUrl = 'http://localhost:8000/api/posts';
  private chart: any;

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    Chart.register(...registerables); // Daftarkan semua modul Chart.js yang diperlukan

    // Inisialisasi chart
    this.chart = new Chart("myChart", {
      type: 'line', // Ubah tipe chart menjadi 'line'
      data: {
        labels: ['Kwh Today', 'Kwh Last', 'Kvarh', 'T-L'],
        datasets: [
          {
            label: 'Blue Line',
            data: [], // Data awal kosong, akan diisi dari API
            borderColor: 'rgba(54, 162, 235, 1)',
            backgroundColor: 'rgba(54, 162, 235, 0.2)',
            borderWidth: 2,
            tension: 0.1, // Mengatur ketegangan garis untuk membuatnya lebih bergerigi
            fill: false, // Tidak mengisi di bawah garis
            pointRadius: 0, // Menghilangkan titik data
            borderDash: [5, 5], // Menambahkan dash untuk membuat efek seismograf lebih nyata
          },
          {
            label: 'Red Line',
            data: [], // Data awal kosong, akan diisi dari API
            borderColor: 'rgba(255, 99, 132, 1)',
            backgroundColor: 'rgba(255, 99, 132, 0.2)',
            borderWidth: 2,
            tension: 0.1,
            fill: false,
            pointRadius: 0,
            borderDash: [5, 5],
          },
          {
            label: 'Green Line',
            data: [], // Data awal kosong, akan diisi dari API
            borderColor: 'rgba(75, 192, 192, 1)',
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
            borderWidth: 2,
            tension: 0.1,
            fill: false,
            pointRadius: 0,
            borderDash: [5, 5],
          }
        ]
      },
      options: {
        scales: {
          y: {
            beginAtZero: true
          }
        },
        elements: {
          line: {
            tension: 0.4 // Tambahkan ketegangan garis untuk membuatnya lebih mulus
          }
        }
      }
    });

    // Panggil fungsi fetchData pertama kali
    this.fetchData();

    // Set interval untuk refresh data setiap 60 detik
    interval(60000).subscribe(() => this.fetchData());
  }

  fetchData(): void {
    this.http.get<any>(this.apiUrl).subscribe(data => {
      console.log('Data from API:', data); // Tambahkan log untuk memeriksa data API

      // Format data sesuai dengan yang diharapkan oleh chart
      const kwhToday = data.map((entry: any) => parseFloat(entry.kwh_today));
      const kwhLast = data.map((entry: any) => parseFloat(entry.kwh_last));
      const kvarh = data.map((entry: any) => parseFloat(entry.kvarh));
      const tl = data.map((entry: any) => parseFloat(entry.tl));

      // Untuk keperluan demo, kita ambil data dari entri pertama saja
      const formattedData = [
        kwhToday[0],
        kwhLast[0],
        kvarh[0],
        tl[0]
      ];

      console.log('Formatted Sensor Data:', formattedData); // Tambahkan log untuk memeriksa data yang diformat

      // Update data chart
      this.chart.data.datasets.forEach((dataset: any) => {
        dataset.data = formattedData;
      });

      // Update chart
      this.chart.update();
    });
  }
}

